package aula06;

public class Exercio02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 7;
		int y = x++;
		
		System.out.println("y: " + y);
		System.out.println("x: " + x);
		
		x = 7;
		y = ++x;
		
		System.out.println("y: " + y);
		System.out.println("x: " + x);

		

	}

}
